DROP DATABASE IF EXISTS movrev_db;
CREATE DATABASE movrev_db;
-- connect via psql
\c movrev_db

-- database configuration
SET statement_timeout = 0;
SET lock_timeout = 0;
SET client_encoding = 'UTF8';
SET standard_conforming_strings = on;
SET check_function_bodies = false;
SET client_min_messages = warning;
SET default_tablespace = '';
SET default_with_oids = false;

CREATE TABLE movies (
    id SERIAL,
    title TEXT NOT NULL,
    industry_rating TEXT NOT NULL,
    theater_release_year TEXT NOT NULL,
    home_release_year TEXT NOT NULL,
    heavy_lang_index TEXT NOT NULL,
    light_lang_index TEXT NOT NULL,
    religious_lang_index TEXT NOT NULL,
    racial_lang_index TEXT NOT NULL,
    PRIMARY KEY(id)
);


CREATE TABLE content_sets (
    id SERIAL,
    movie_id INT UNIQUE NOT NULL,
    language TEXT,
    violence TEXT,
    sex TEXT,
    drugs TEXT,
    PRIMARY KEY(id)
);

CREATE TABLE accounts (
    id SERIAL,
    email TEXT UNIQUE NOT NULL,
    password TEXT NOT NULL,
    PRIMARY KEY(id)
);

CREATE TABLE users (
    id SERIAL,
    first_name TEXT NOT NULL,
    birthday DATE NOT NULL,
    account_id INT NOT NULL,
    PRIMARY KEY(id)
);

CREATE TABLE user_interactions (
    id SERIAL,
    user_comment TEXT,
    user_watched INT,
    user_rating INT,
    user_id INT NOT NULL,
    movie_id INT NOT NULL,
    PRIMARY KEY(id)
);

ALTER TABLE content_sets
ADD CONSTRAINT fk_content_sets_movies 
FOREIGN KEY (movie_id) 
REFERENCES movies;

ALTER TABLE users
ADD CONSTRAINT fk_users_accounts 
FOREIGN KEY (account_id) 
REFERENCES accounts;

ALTER TABLE user_interactions
ADD CONSTRAINT fk_ui_users 
FOREIGN KEY (user_id) 
REFERENCES users;

ALTER TABLE user_interactions
ADD CONSTRAINT fk_ui_movies 
FOREIGN KEY (movie_id) 
REFERENCES movies;


